import "./App.css";
import { useState } from "react";
import Sidebar from "./components/Sidebar.js";
import Cards from "./components/Cards.js";
import Heading from "./components/Heading.js";

import AddModal from "./components/AddModal.js";

function App() {
  const [addShowing, setAddShowing] = useState(false);
  const [songs, setSongs] = useState([
    {
      id: 1,
      name: "Something",
      artist: "The Beatles",
      chords: "",
      youtube: "",
      status: "Learned",
      notes: "",
      lyrics: "",
    },
    {
      id: 2,
      name: "Neon",
      artist: "John Mayer",
      chords: "",
      youtube: "",
      status: "In Progress",
      notes: "",
      lyrics: "",
    },
    {
      id: 3,
      name: "Pride and Joy",
      artist: "Stevie Ray Vaughn",
      chords: "",
      youtube: "",
      status: "In Progress",
      notes: "",
      lyrics: "",
    },
    {
      id: 4,
      name: "Pride and Joy",
      artist: "Stevie Ray Vaughn",
      chords: "",
      youtube: "",
      status: "In Progress",
      notes: "",
      lyrics: "",
    },
    {
      id: 5,
      name: "Pride and Joy",
      artist: "Stevie Ray Vaughn",
      chords: "",
      youtube: "",
      status: "In Progress",
      notes: "",
      lyrics: "",
    },
  ]);

  const toggleAddShowing = () => {
    setAddShowing(!addShowing);
  };

  const saveSong = () => {
    setAddShowing(!addShowing);
    console.log("save new song info");
  };

  const addSong = (song) => {
    setAddShowing(false);
    console.log("new song added");
    setSongs([
      ...songs,
      {
        id: 5,
        name: "Pride and Joy",
        artist: "Stevie Ray Vaughn",
        chords: "",
        youtube: "",
        status: "In Progress",
        notes: "",
        lyrics: "",
      },
    ]);
  };

  return (
    <div className="App">
      <Sidebar toggle={toggleAddShowing} />
      {addShowing && (
        <AddModal
          toggle={toggleAddShowing}
          save={saveSong}
          onAddSong={addSong}
        />
      )}

      <Heading />
      <div className="container">
        <div className="row cards-container offset-3 d-flex">
          <Cards songs={songs} />
          {/* {songs.length === 0 ? (
            <p>No Songs to Show</p>
          ) : (
            <Cards songs={songs} />
          )} */}
        </div>
      </div>
    </div>
  );
}

export default App;
