import "./App.css";
import { useState } from "react";
import Sidebar from "./components/Sidebar.js";
import Card from "./components/Card.js";
import AddModal from "./components/AddModal.js";

function App() {
  const [addShowing, setAddShowing] = useState(false);
  const [songs, setSongs] = useState([
    {
      id: 1,
      song: "Something",
      artist: "The Beatles",
      chords: "",
      youtube: "",
      status: "",
      lyrics: "",
    },
    {
      id: 2,
      song: "Something",
      artist: "The Beatles",
      chords: "",
      youtube: "",
      status: "",
      lyrics: "",
    },
    {
      id: 3,
      song: "Something",
      artist: "The Beatles",
      chords: "",
      youtube: "",
      status: "",
      lyrics: "",
    },
  ]);

  const toggleAddShowing = () => {
    setAddShowing(!addShowing);
  };

  const saveSong = () => {
    setAddShowing(!addShowing);
    console.log("save new song info");
  };

  return (
    <div className="App">
      <Sidebar toggle={toggleAddShowing} />
      {addShowing && <AddModal toggle={toggleAddShowing} save={saveSong} />}

      <div className="offset-3">
        <h1>PlayerList</h1>
      </div>
      <div className="container">
        <div className="row offset-3 d-flex justify-content-around">
          <Card songs={songs} />
          <p>
            "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do
            eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim
            ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut
            aliquip ex ea commodo consequat. Duis aute irure dolor in
            reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla
            pariatur. Excepteur sint occaecat cupidatat non proident, sunt in
            culpa qui officia deserunt mollit anim id est laborum."
          </p>
        </div>
      </div>
    </div>
  );
}

export default App;
